const { user, pass } = msg.payload;
const sid = msg.socketid;

// Get user list
let users = global.get("users") || {};

if (users[user] && users[user].password === pass) {
    // Login success
    flow.set("loggedIn_" + sid, true);
    flow.set("lastLoginUser_" + sid, user);

    // Instead of returning the ui_control directly, pass through for delay
    return {
        payload: "Login successful", // or just use a neutral message
        topic: "loginSuccess",
        socketid: sid
    };

} else {
    return {
        payload: "Invalid credentials",
        topic: "loginError",
        socketid: sid
    };
}

if (msg.topic === "loginError") {
    return [msg, null]; // Send to login_template for display
}

if (msg.topic === "loginSuccess") {
    return [null, msg]; // Send to delay before tab switch
}

const sid = msg.socketid;

flow.set("loggedIn_" + sid, false);
flow.set("lastLoginUser_" + sid, null);
flow.set("selectedTrainId_" + sid, null);

return {
    payload: { tab: "Login" },
    topic: "ui_control",
    socketid: sid
};

const { user, pass } = msg.payload;
if (!user || !pass) return null;

let users = global.get("users") || {};

if (users[user]) {
    return {
        payload: "Username already exists.",
        topic: "signupError",
        socketid: msg.socketid
    };
}

// Save new user
users[user] = {
    password: pass,
    createdAt: new Date().toISOString()
};
global.set("users", users);

return {
    payload: { tab: "Login" },
    topic: "ui_control",
    socketid: msg.socketid
};

if (msg.topic === "signupError") {
    return [msg, null];
}
return [null, msg];

return {
    payload: { tab: "Signup" },
    topic: "ui_control",
    socketid: msg.socketid
};

return {
    payload: { tab: "Login" },
    topic: "ui_control",
    socketid: msg.socketid
};

return {
  payload: { tab: "Home" },
  topic: "ui_control",
  socketid: msg.socketid
};

let sid = msg.socketid;
msg.loggedIn = flow.get("loggedIn_" + sid) || false;
return msg;

return {
    payload: { tab: "Login" },
    topic: "ui_control",
    socketid: msg.socketid
};

return {
    payload: { tab: "Login" },
    topic: "ui_control",
    socketid: msg.socketid
};

return {
  topic: "triggerRefreshGuard",
  socketid: msg.socketid
};

// This node is the heart of the backend logic.

// 1. Get the current cache of all train statuses
let trains = flow.get('trains') || {};

// 2. Get trainId from topic
const topicParts = (msg.topic || "").split('/');
const trainId = topicParts[1];

// --- DEFINITIVE FIX FOR UNDEFINED TRAIN ---
// If trainId is empty, null, or literally the string "undefined", kill the message.
if (!trainId || trainId === 'undefined') { 
    return null; 
}

// 3. Update the cache with the new status
trains[trainId] = msg.payload;

// 4. Save the updated cache
flow.set('trains', trains);

// 5. Create a broadcast message to send the *entire* state to all clients
let broadcastMsg = {
    topic: 'update',
    payload: trains
};

return broadcastMsg;

// When a new client connects, we send them the current complete state.
let trains = flow.get('trains') || {};

msg.topic = 'update';
msg.payload = trains;

return msg;

// This node receives actions from the ui_template

// 1) two outputs defined on the node
//    Output 1 ‚Üí ui_template
//    Output 2 ‚Üí mqtt out

if (msg.topic !== "ui_action") return null;

const { trainId, property, value } = msg.payload;
let trains = flow.get('trains') || {};
let logs = global.get("iotLogs") || [];

logs.unshift({
    timestamp: new Date().toLocaleString(),
    user: flow.get("lastLoginUser_" + msg.socketid) || "unknown",
    trainId: trainId,
    action: property,
    value: value
});

global.set("iotLogs", logs);
// 2) DELETE branch: remove and update UI only
if (property === 'deleteTrain') {
    if (trains[trainId]) {
        delete trains[trainId];
        flow.set('trains', trains);
    }
    // [ sendToUI, sendToMQTT ]
    return [
        { topic: 'update', payload: trains },  // goes to ui_template
        null                                   // nothing to MQTT
    ];
}

// 3) NORMAL status-update branch
let currentStatus = trains[trainId] || { trainId };
currentStatus[property] = value;

// recalc safety logic‚Ä¶
let isBlocked =
    (currentStatus.partiesInside > 0) ||
    currentStatus.shedPlugFront ||
    currentStatus.shedPlugRear ||
    currentStatus.wheelChock ||
    currentStatus.componentRemoved ||
    currentStatus.undercarriageWork ||
    currentStatus.roofWork;

currentStatus.doNotPowerOn = isBlocked;
currentStatus.movementAllowed = !isBlocked;

// save back
trains[trainId] = currentStatus;
flow.set('trains', trains);

// prepare MQTT message
const mqttMsg = {
    ...msg,
    topic: `train/${trainId}/status`,
    payload: currentStatus
};

// If you want the UI to refresh on every change (optional), send to Out 1 as well:
const uiMsg = { topic: 'update', payload: trains };

// Return [ UI-msg, MQTT-msg ]
return [
    uiMsg,     // goes to ui_template
    mqttMsg    // goes to your MQTT out node
];

if (msg.topic && msg.topic.includes('undefined')) {
    return; 
}

const fullStatus = msg.payload;

// Define the exact keys the Arduino expects, in order.
const requiredKeys = [
    'partiesInside',
    'shedPlugFront',
    'shedPlugRear',
    'wheelChock',
    'componentRemoved',
    'undercarriageWork',
    'roofWork',
    'doNotPowerOn',
    'movementAllowed'
];

const cleanPayload = {};

// Build the new payload object with only the required keys.
requiredKeys.forEach(key => {
    // Ensure the key exists in the source object, default to false if not.
    if (fullStatus.hasOwnProperty(key)) {
        cleanPayload[key] = fullStatus[key];
    } else {
        // This is a safety measure, should not happen in normal operation.
        cleanPayload[key] = (key === 'partiesInside') ? 0 : false;
    }
});

// Replace the old payload with the clean one.
msg.payload = cleanPayload;

return msg;

// 1) Extract trainId
const parts = (msg.topic||"").split('/');
if (parts.length < 4) return null;
const trainId = parts[1];

// 2) Read the flag
const ignore = !!msg.payload.ignoreCommands;

// 3) Broadcast to all dashboards
msg.topic = 'emergency';
msg.payload = { trainId, ignore };
delete msg.socketid;
return msg;

let logs = global.get("iotLogs") || [];
msg.topic = "iotLogs";
msg.payload = logs;
return msg;

const sid = msg.socketid || "";
const user = flow.get("lastLoginUser_" + sid);

if (user !== "admin") {
    return {
        topic: "unauthorizedClear",
        socketid: sid
    };
}

// Show confirmation modal (instead of clearing immediately)
return {
    topic: "confirmClearLogs",
    socketid: sid
};

const sid = msg.socketid || "";
const user = flow.get("lastLoginUser_" + sid);

if (user === "admin") {
    global.set("iotLogs", []);
    return {
        topic: "iotLogs",
        payload: [],
        socketid: sid
    };
}

return null;
